class ShareFile(object):

	def __init__(self):
		self.id		= None
#		self.share_guid = None
		self.parent_guid = ""
		self.guid	= ""
		self.name	= ""
#		self.path	= ""
		self.is_dir	= "0"
		self._date_created = ""
		self._complete_path = ""
		self._acl_subjects	= None # groups and users in one list, those who have access to this object
		self._parent 		= None
		self._is_top_folder	= None

	# STORAGE
	@property
	def complete_path(self):
		if self._complete_path is "":
			self._complete_path = "/".join((self.parent_guid,self.guid))
		return self._complete_path

	@property
	def date_created(self):
		bad_dir_string = "---"
		if not self._date_created:
			full_path = self.complete_path
			if self.exists():
				self._date_created = date.fromtimestamp(os.stat(application.storage.abs_path(self.complete_path)).st_mtime ).strftime("%d / %m / %Y")
			else:
				self._date_created = bad_dir_string
		return self._date_created

	@property
	def acl_subjects(self):
		from class_user import User
		from class_group import Group
		if self._acl_subjects is None:
			acls = ACL_record.retrieve(object=self.guid)
			self._acl_subjects = [
				User.get_by_guid(acl.subject) or Group.get_by_guid(acl.subject) for acl in acls
			]
		return self._acl_subjects

	@property
	def is_top_folder(self):
		self._is_top_folder = True if not self.parent_guid else False
		return self._is_top_folder

	@property
	def parent(self):
		if not self._parent:
			self._parent = ShareFile.__retrieve_one(guid=self.parent_guid, is_dir="1")
		return self._parent

	def __getitem__(self, key):
		return self.__dict__[key] if key in self.__dict__ else None

	def __str__(self):
		return self.name.encode("utf8")#u"( %s )" % self.name.encode("utf8")

	def __fill_from_row(self, row):
		(self.id, self.parent_guid, self.guid, self.name, self.is_dir) = row
		return self

	def fill_from_row(self, row):
		(self.id, self.parent_guid, self.guid, self.name, self.is_dir) = row
		return self

	def __insert(self):
		self.id = CRUD.create(TABLE_NAME,
			parent_guid=self.parent_guid, guid=self.guid,
			name=self.name, is_dir="0")
		return self

	def __update(self):
		CRUD.update(TABLE_NAME, {"id": self.id},
			parent_guid=self.parent_guid,
			name=self.name)
		return self

	@staticmethod
	def __retrieve(**kwargs):
		rows = CRUD.retrieve(TABLE_NAME, **kwargs)
		return [ShareFile().__fill_from_row(row) for row in rows]

	@staticmethod
	def __retrieve_one(**kwargs):
		from engine_core import global_log
		try:
			global_log.insert(0,
				"class:%s, <br/> method: %s" % (
					"ShareFile",
					str(["%s:%s:%s"%(a, kwargs[a], typeof(kwargs[a])) for a in kwargs])
			))
		except:
			pass
		row = CRUD.retrieve_one(TABLE_NAME, **kwargs)
		return ShareFile().__fill_from_row(row) if row else None

	def __delete(self):
		CRUD.delete(TABLE_NAME, guid=self.guid)
		return self

	def save(self, file_handler=""):
		if not self.name:
			raise Exception("FS_EMPTY_NAME")

		if self.id:
			self.__update()
			if file_handler:
				self.add_file(file_handler)		# actually, previous file would be replaced
		elif file_handler:
			self.guid = str(uuid4()) 			# define guid of file
			self.is_dir = "0"
			self.add_file(file_handler)			# saving file to disk
			self.__insert()						# add record  to db
		else:
			raise Exception("EMPTY_FILE_HANDLER")

		return self

	def delete(self):
		self.file_delete() 	# physical deletion of the file
		self.__delete()		# from Share_file db
		# acl table
		ACL_record.delete( object=self.guid )


	@staticmethod
	def get_by_id(share_file_id):
		return ShareFile.__retrieve_one(id=share_file_id, is_dir="0")

	@staticmethod
	def get_by_guid(share_file_guid):
		return ShareFile.__retrieve_one(guid=share_file_guid, is_dir="0")

	@staticmethod
	def get_by_parent_guid(share_file_parent_guid):
		return ShareFile.__retrieve_one(guid=share_file_parent_guid, is_dir="0")

#	@staticmethod
#	def get_by_share_guid(share_file_share_guid):
#		return ShareFile().__retrieve_one(guid=share_file_share_guid, is_dir="0")

	@staticmethod
	def all(limit="", offset=""):
		return ShareFile.__retrieve(limit=limit, offset=offset, is_dir="0")

	# STORAGE
	def add_file(self, file_handler):
		application.storage.write(self.complete_path, file_handler)

	# STORAGE
	def file_delete(self):
		if not self.exists():
			return "FILE_NOT_FOUND" # file already deleted or wrong path
		application.storage.delete(self.complete_path)
		return True

	def exists(self):
		return application.storage.exists(self.complete_path)

	def parent_dir(self): 	raise Exception("NOT_IMPLEMENTED")

	def handler(self):
		return application.storage.open(self.complete_path)

	def link(self): 		raise Exception("NOT_IMPLEMENTED")

	def abs_path(self): 	raise Exception("NOT_IMPLEMENTED")

	def size(self): 		raise Exception("NOT_IMPLEMENTED")

	def move_to(self, folder):
		a = application.storage.abs_path(self.complete_path)
		b = application.storage.abs_path(folder.complete_path)

		shutil.move(a, b)
		self.parent_guid = folder.guid
		self.save()

	def copy_to(self): 		raise Exception("NOT_IMPLEMENTED")

	# STORAGE
	def download(self):
		if not self.exists():
			raise Exception("FILE_NOT_FOUND") # file already deleted or wrong path

		response.send_file(
				self.name.encode("UTF8"),
				self.size(),
				self.handler(),
				self.mime()
			)

	def mark(self): raise Exception("NOT_IMPLEMENTED")

	def print_file(self): raise Exception("NOT_IMPLEMENTED")

	def rename(self): raise Exception("NOT_IMPLEMENTED")

	def users_acl(self):
		from class_user import User
		return [subject for subject in self.acl_subjects if isinstance(subject, User)]

	def groups_acl(self):
		from class_group import Group
		return [subject for subject in self.acl_subjects if isinstance(subject, Group)]

	def subjects_acl(self):
		return [subject for subject in self.acl_subjects]

	def add_access_to(self, subject_guid, rule):
		new_acl = ACL_record()
		new_acl.subject = subject_guid
		new_acl.object 	= self.guid
		new_acl.rule 	= rule
		new_acl.save()

	def delete_access_for(self, subject_guid, rule=""):
		ACL_record.delete(
			subject=subject_guid, object=self.guid, rule=rule
		)

	def mime(self):
		return MIME_TABLE[ self.extension() ] if self.extension() in MIME_TABLE else "application/octet-stream"

	def extension(self):
		root,fextension = os.path.splitext( self.name )
		return fextension.lower()

	def size(self):
		""" returns a size of a current ShareFile in bytes """
		return application.storage.getsize(self.complete_path)

PAGE_TITLE = """"%s &raquo; %s" """

class WidgetPageTitle(object):

	def __init__(self, page):
		self.__page = page
		self.__title_key = ""

	def set_data(self, title_key):
		self.__title_key = title_key

	def render(self):
		import localization
		loc = localization.get_lang()

		self.__page.title = PAGE_TITLE % (
			loc["header_hgmb"],
			loc.get(self.__title_key, "Unknown page")
		)

from class_CRUD import CRUD

TABLE_NAME = "workflow_library"

class WorkflowLibrary(object):

	def __init__(self):
		self.id		= None
		self.name	= ""
		self.source	= ""
		self.description	= ""


	def __fill_from_row(self, row):
		(self.id, self.name, self.source, self.description) = row
		return self

	def __insert(self):
		self.id = CRUD.create(TABLE_NAME, name=self.name, source=self.source, description=self.description)
		return self

	@staticmethod
	def __retrieve(self, **kwargs):
		rows = CRUD.retrieve(TABLE_NAME, **kwargs)
		return [WorkflowLibrary().__fill_from_row(row) for row in rows]

	@staticmethod
	def __retrieve_one(self, **kwargs):
		row = CRUD.retrieve_one(TABLE_NAME, **kwargs)
		return WorkflowLibrary().__fill_from_row(row) if row else None

	def __update(self):
		CRUD.update( TABLE_NAME,
			{"id": self.id},
			name=self.name, source=self.source, description=self.description
		)
		return self

	def __delete(self):
		CRUD.delete(TABLE_NAME, name=self.name)
		return self

	def save(self):
		if not self.name:
			raise Exception("WORKFLOW_EMPTY_NAME")

		if self.id:
			if WorkflowLibrary.get_by_name(self.name) is not None:
				raise Exception("WORKFLOW_NAME_MUST_BE_UNIQUE")
			self.__update()
		else:
			self.__insert()

		return self

	def delete(self):
		return self.__delete()

	@staticmethod
	def get_by_id(library_id):
		return WorkflowLibrary().__retrieve_one(id=library_id)

	@staticmethod
	def get_by_name(library_name):
		return WorkflowLibrary().__retrieve_one(name=library_name)

"""
	WorkflowInstance.

