from class_CRUD import CRUD
from utils.uuid import uuid4
import os, os.path, shutil

TABLE_NAME = "workflow_instance"

class WorkflowInstance(object):

	STATES = {
		"stopped" 	: 0,
		"pause" 	: 1,
		"active" 	: 2,
		"finished" 	: 3
	}


	def __init__(self):
		# database info
		self.id		= None
		self.workflow_guid	= ""
		self.author_guid	= ""
		self.current_user_guid = ""
		self.document_guid	= ""
		self.guid			= ""
		self.status 		= self.STATES["stopped"]
		self.state 			= "no state"

		# properties (lazy loading)
		self._workflow_class= None	# Instance of a WorkflowInstance class
		self._author		= None	# Author
		self._current_user	= None
		self._document		= None	# instance of a current document
		self._path			= ""	# path for storage versions of documents

	def __fill_from_row(self, row):
		(	self.id, self.workflow_guid, self.author_guid,
			self.current_user_guid, self.document_guid,
			self.guid, self.status, self.state
		) = row
		return self

	def __insert(self):
		self.guid = str(uuid4())
		self.id = CRUD.create(TABLE_NAME,
			workflow_guid		= self.workflow_guid,
			author_guid			= self.author_guid,
			current_user_guid	= self.current_user_guid,
			document_guid		= self.document_guid,
			status				= self.status,
			state				= self.state,
			guid				= self.guid
		)
		return self

	@staticmethod
	def __retrieve(**kwargs):
		rows = CRUD.retrieve(TABLE_NAME, **kwargs)
		return [WorkflowInstance().__fill_from_row(row) for row in rows]

	@staticmethod
	def __retrieve_one(**kwargs):
		row = CRUD.retrieve_one(TABLE_NAME, **kwargs)
		return WorkflowInstance().__fill_from_row(row) if row else None

	def __update(self):
		CRUD.update( TABLE_NAME,
			{"id": self.id},
			workflow_guid		= self.workflow_guid,
			author_guid			= self.author_guid,
			current_user_guid	= self.current_user_guid,
			document_guid		= self.document_guid,
			status				= self.status,
			state				= self.state,
		)
		return self

	def __delete(self):
		CRUD.delete(TABLE_NAME, id=self.id)
		return self

	def save(self):
		if self.id:
			self.__update()
		else:
			os.makedirs(self.path)
			self.__insert()

		return self

	def delete(self):
		if os.path.exists(self.path):
			shutil.rmtree(self.path)
		return self.__delete()

	# PROPERTIES

	@property
	def workflow_class(self):
		if not self.workflow_guid:
			return ""
		if not self._workflow_class:
			from class_workflow import Workflow
			self._workflow_class = Workflow.get_by_guid(self.workflow_guid)
		return self._workflow_class

	@workflow_class.setter
	def workflow_class(self, value):
		from class_workflow import Workflow
		if isinstance(value, Workflow):
			self._workflow_class 	= value
			self.workflow_guid 		= value.guid

	@property
	def author(self):
		if not self.author_guid:
			return ""
		if not self._author:
			from class_user import User
			self._author = User.get_by_guid(self.author_guid)
		return self._author

	@author.setter
	def author(self, value):
		from class_user import User
		if isinstance(value, User):
			self._author 	= value
			self.author_guid= value.guid

	@property
	def current_user(self):
		if not self.current_user_guid:
			return ""
		if not self._current_user:
			from class_user import User
			self._current_user = User.get_by_guid(self.current_user_guid)
		return self._current_user

	@current_user.setter
	def current_user(self, value):
		from class_user import User
		if isinstance(value, User):
			self._current_user 		= value
			self.current_user_guid 	= value.guid

	@property
	def document(self):
		if not self.document_guid:
			return ""
		if not self._document:
			from class_share_file import ShareFile
			self._document = ShareFile.get_by_guid(self.document_guid)
		return self._document

	@document.setter
	def document(self, value):
		from class_share_file import ShareFile
		if isinstance(value, ShareFile):
			self._document 		= value
			self.document_guid 	= value.guid

	@property
	def path(self):
		if not self._path:
			self._path = os.path.join(
				VDOM_CONFIG["SHARE-DIRECTORY"],
				self.workflow_guid,
				self.guid
			)
		return self._path

	def set_state(self, key):
		if not key in self.STATES: return
		self.state = self.STATES[key]

	# GETTIES

	@staticmethod
	def all():
		return WorkflowInstance().__retrieve()

	@staticmethod
	def get_by_id(workflow_id):
		return WorkflowInstance().__retrieve_one(	id = workflow_id )

	@staticmethod
	def get_by_guid(workflow_guid):
		return WorkflowInstance().__retrieve_one(	guid = workflow_guid )

	@staticmethod
	def get_by_workflow_guid(wf_guid):
		return WorkflowInstance().__retrieve(	workflow_guid = wf_guid )


	# DOCUMENT METHODS

	def documents(self):
		pass

	def actual_document(self):
		if not os.path.exists(self.path):
			raise Exception("FILE_NOT_FOUND") # file already deleted or wrong path
		count = len( os.listdir(self.path) )
		if count != 0:
			full_path = so.path.join( self.path, str(count - 1) )
		else:
			full_path = self.document.complete_path

		if os.path.isfile(full_path):
			obsolete_request.send_file(
				self.name.encode("UTF8"),
				os.stat(full_path).st_size,
				open(full_path),
				self.mime()
			)

	def clean_documents(self):
		pass

	def add_document(self, doc_data):
		count = len( os.listdir(self.path) )
		full_path = so.path,join( self.path, str(count) )

		f = open(full_path, "w")
		if  type(doc_data) == types.FileType or hasattr(doc_data, "read"):
			shutil.copyfileobj(doc_data, f)
		else:
			f.write(doc_data)#.encode("utf8"))
		f.close()

import traceback, sys, cgi

class Trace:
	@classmethod
	def exception_trace( self, html_result=True ):
		type, val, tb = sys.exc_info()
		str = ['\n', '<br/>'][1 if html_result else 0].join( traceback.format_exception( type, val, tb ))
		return str#cgi.escape( str )

	@classmethod
	def stack_trace( self ):
		str = '\n'.join( traceback.format_stack() )
		return cgi.escape( str )

import re

"""

* Classes for working with email *

Usage example:

from class_mail import Mailer, Message

# create mailer
mailer = Mailer()

# create & init message
msg = Message()
msg.set_to_email("petrov@mail.ru")
msg.set_from_email("scales@gmail.com")
msg.set_subject("Greetings")
msg.set_text("Hi, people!")

# send message
mailer.send(msg)

"""

class Mailer:

	def __init__(self):
		pass

	def send(self, msg):

		from_email = msg.get_from_email()
		to_email = msg.get_to_email()
		subject = msg.get_subject()
		text = msg.get_text()
		attachments = msg.get_attachments()

		if isinstance(to_email, (list, tuple)):
			for to_subemail in to_email:
				server.mailer.send(from_email, to_subemail, subject, text, attachments)
		else:
			server.mailer.send(from_email, to_email, subject, text, attachments)


class Message(object):

	def __init__(self):

		self._from_email = None
		self._to_email = None
		self._subject = None
		self._text = None
		self._attachments = None

	def check_email(self, email):
		""" Checks if a specified email's format is valid """

		if len(email)>=6 or len(email)<=100:
			emailf=email.find('@')
			if not emailf==-1:
				email_parts = email.split("@")

				if len(email_parts) != 2:
					return False
				else:
					if email_parts[0]=="" or email_parts[1]=="":
						return False
					else:
						match = re.match(r"\b[A-Za-z0-9._+-]{1,100}", email_parts[0])
						if email_parts[0][-1]==".":
							return False
						else:
							if email_parts[1][0]==".":
								return False
							else:
								if not match or len(match.group())<len(email_parts[0]):
									return False
								else:
									if not email_parts[1].find(".")==-1:
										match = re.match(r"[A-Za-z0-9\-\.]+", email_parts[1])
										if not match or len(match.group())<len(email_parts[1]):
											return False
										else:
											match = re.search(r"\.\.", email_parts[1])
											if not match:
												tchk = email_parts[1][::-1].find(".")
												if tchk>=2 and tchk<=4:
													match = re.match(r"\.[A-Za-z]{2,4}\b", email_parts[1][-(tchk+1):])
													if not match or len(match.group())<len(email_parts[1][-(tchk+1):]):
														return False
													else:
														return True
												else:
													return False
											else:
												return False
									else:
										return False
			else:
				return False
		else:
			return False

	def get_default_from_email(self):
		return "noreply@" + request.server.host.encode("utf-8")


	def get_from_email(self):
		return self._from_email if self._from_email else self.get_default_from_email()

	def set_from_email(self, email):

		if not self.check_email(email):
			raise MailFormatError("Invalid email format.")

		self._from_email = email


	def get_to_email(self):
		return self._to_email

	def set_to_email(self, email):

		if isinstance(email, (list, tuple)):
			if not all(self.check_email(m) for m in email):
				raise MailFormatError("Invalid email format.")
		else:
			if not self.check_email(email):
				raise MailFormatError("Invalid email format.")

		self._to_email = email


	def get_subject(self):
		return self._subject

	def set_subject(self, subject):
		self._subject = subject


	def get_text(self):
		return self._text

	def set_text(self, text):
		self._text = text


	def get_attachments(self):
		return self._attachments if self._attachments else [ ]

	def set_attachments(self, attachments):
		self._attachments = attachments

class MailFormatError(Exception):

	def __init__(self, msg):
		self.message = msg

	def __str__(self):
		return self.message

import shutil

class EOFAPI:

	@staticmethod
	def move_document(	workflow_id, document_guid, folder_from_guid, folder_to_guid, users=None, set_right=True,
						keep_copy=True, keep_version_source=True, keep_version_target="version_folder_guid"):
		from engine_core import global_log, engine
		from class_share_file import ShareFile
		from class_share_dir import ShareDir
		from class_xml_action import xml_action

		file 		= ShareFile.get_by_guid(str(document_guid))
		to_folder 	= ShareDir.get_by_guid(str(folder_to_guid))
		file.move_to(to_folder)

		if users:
			download_file = xml_action()
			download_file.set_data("download_file",
				workflow_id = workflow_id, folder_id = to_folder.guid, file_id = file.guid
			)
			download_file_xml = download_file.render()
			engine.log(workflow_id, download_file_xml)
			for user in users:
				engine.add_action( user().guid, download_file_xml )

		if set_right:
			for user in users:
				engine.log(workflow_id,"user_rule='%s', parent='%s'"%(user().rules["SEE"], str(file.parent.name)))
				file.add_access_to(user().guid, str(user().rules["SEE"]))
				share_obj = file
				while share_obj.parent:
					p = share_obj.parent
					engine.log(workflow_id,"user_rule='%s', parent='%s'"%(user().rules["SEE"], str(share_obj.parent.name)))
					rule = user().rules["READ"] if p.is_top_folder else user().rules["SEE"]
					p.add_access_to(user().guid, str(rule))
					share_obj = share_obj.parent

	@staticmethod
	def show_dialog(title, message):
		return "%s -- %s" % (title, message)

	@staticmethod
	def send_mail(workflow_id, users, email_title, email_content, email_attach, email_doc):
		from engine_core import engine
		from class_share_file import ShareFile

		engine.workflow_log[workflow_id].insert(0, "::".join([workflow_id, str(users), email_title, email_content, str(email_attach), str(email_doc)]))

		if not users: return
		email_from = "workflow@vdombox.ru"

		#attuple=(open(zipfilename, "rb").read(),name,"application/zip","zip")
		attachment = []
		if email_attach:
			engine.log(workflow_id,"trying to found and attach doc with id: %s"%email_doc)
			doc = ShareFile.get_by_guid(str(email_doc))
			if doc:
				engine.log(workflow_id,"Doc found!")
				attachment = [(open(application.storage.abs_path(doc.complete_path), "rb").read(), doc.name.encode("utf8"), doc.mime(), doc.extension())]

		for user in users:
			engine.log(workflow_id,"trying send mail to <b>%s</b>"%user().email)
			server.mailer.send(email_from, user().email, email_title, email_content, attachment)
			engine.log(workflow_id,"Mail sent!")

	@staticmethod
	def send_popup(workflow_id, users, content):
		from class_xml_action import xml_action
		from engine_core import engine, global_log

		if not users: return
		notify = xml_action()
		notify.set_data("notify",
			workflow_id = workflow_id, content = content
		)
		notify_xml = notify.render()
		engine.log(workflow_id,  notify_xml)

		for user in users:
			engine.log(workflow_id,"Trying to put action into queue for user: <b>%s</b>"%user().email)
			engine.add_action( user().guid, notify_xml )

import Queue, time

import os.path
import sys
import managers
from utils.semaphore import VDOM_semaphore

workflow_queue 	= Queue.Queue()
global_log		= []

FileEvent = 1; ReplyEvent = 2; MoveDoneEvent = 3; ArchiveDoneEvent = 4;
FileEventInsert = 1; FileEventModify = 2;

class WorkflowEngine:

	def __init__(self):
		#self.current_wf = wfclass.SimpleWorkflow()
		self.listeners 			= {}
		self.folder_trigers 	= {}
		self.file_locks 		= {}
		self.running_workflows 	= {}
		self.workflow_log 		= {}
		self.actions			= {}
		self.actions_lock		= VDOM_semaphore()
		#sys.path.insert(0,os.path.join(VDOM_CONFIG["LIB-DIRECTORY"],application.id,"WORKFLOWS_CLASSES"))

		#thread1 = threading.Thread(target=self.process_queue)
		#thread1.start()
		#managers.log_manager.info_bug("Workflow Engine was started", "WF Engine")

	def process_queue(self):
		application.set_app_id("c1d6041c-6b33-49e8-ac72-8df57ac01304")
		while True:
			global_log.insert(0,"Waiting for new event..." )
			event = workflow_queue.get(True)
			global_log.insert(0,"Engine process new event: %s" % (event.event_type,))
			if event.workflow_id:
				if (event.workflow_id, event.event_type) in self.listeners:
					workflow = self.running_workflows.get(event.workflow_id)
					if not workflow:
						msg = "No running workflow with such id %s"%(event.workflow_id)
						global_log.insert(0,"Engine error: %s" % msg)
						continue
					workflow.current_event = event
					try:
						ret = self.run_workflow(workflow, self.listeners[(event.workflow_id, event.event_type)])
						if ret:
							del self.running_workflows[event.workflow_id]
							msg = "Workflow %s is lucky to be ended!" % event.workflow_id
							global_log.insert(0,msg)
							self.log(event.workflow_id, msg)
					except Exception as e:
						from class_trace import Trace
						err = "Engine error: %s on wf '%s' and event '%s'" % (str(e),event.workflow_id, event.event_type)
						global_log.insert(0,err+"<br/>"+Trace.exception_trace())
						self.log(event.workflow_id, err)
				else:
					msg = "No valid dispatcher for %s in workflow %s"%(event.event_type,event.workflow_id)
					global_log.insert(0,"Engine error: %s" % msg)
			else:
				global_log.insert(0,"-------- > 1")
				if (event.mapped_folder,event.event_type) in self.folder_trigers:
					try:
						from class_xml_event import xml_event
						workflow = self.folder_trigers[(event.mapped_folder,event.event_type)]()
						self.running_workflows[workflow.id] = workflow
						self.run_workflow(workflow,workflow.initialstep)
						event.workflow_id = workflow.id

						global_log.insert(0,event.toxml().replace("<", "&lt;"))
						#:::::::::::::::::::::::::::::::
						workflow_queue.put(xml_event(event.toxml()))
						#:::::::::::::::::::::::::::::::
						self.workflow_log[event.workflow_id] = []

					except Exception as e:
						from class_trace import Trace
						global_log.insert(0,"-------- > exception <br/>"+Trace.exception_trace())
						global_log.insert(0,"Engine error while creating new workflow: %s" % str(e))

				global_log.insert(0,"-------- > 8")
			time.sleep(0.2)


	def run_workflow(self, workflow, start_state):
		need_event =  False
		while not need_event:
			workflow.currentstep()
			if workflow.currentstep in workflow.finalsteps:
				return True #
			for transition in workflow.transitionlist[workflow.currentstep]:
				next_step = transition()
				if next_step:
					workflow.currentstep = next_step
					if next_step in workflow.steplist and workflow.steplist[next_step] in ("documentEvent",):
						need_event = True
						self.register_event(workflow, next_step,"insert")
					break

	def run_workflow_old(self):
		need_event =  False
		while not need_event:
			self.current_wf.currentstep()
			need_event = True
			for transition in self.current_wf.transitionlist[self.current_wf]:
				next_step = transition()
				if next_step:
					self.current_wf.currentstep = next_step
					need_event =  False
					break
			if need_event:
				raise Exception("No transition")

	def log(self, workflow_id, msg): # modify to double parameters: key, value
		if workflow_id in self.workflow_log:
			self.workflow_log[workflow_id].insert(0,msg)

	def check_condition(self, workflow, condition):
		return True

	def register_global(self, workflow_class, folder_and_operation):
		self.folder_trigers[folder_and_operation] = workflow_class

	def register_event(self, workflow, dispatcher, event_type):
		self.listeners[(workflow.id,event_type)] = dispatcher

	def unregister_event(self, workflow, event_type):
		del self.listeners[(workflow.id,event_type)]

	def register_timeout(self, workflow, dispatcher, time):
		pass

	def get_wf_object(self):
		pass

	def load_workflow(self, wf_name):
		try:
			global_log.insert(0,u"Loading WF '%s'"%wf_name)
			exec (
"""import %(wf)s
%(wf)s.%(wf)s.initiate_listener()"""%{"wf":wf_name}
			)
			return True, ""
		except Exception as e:
			from class_trace import Trace
			import traceback
			global_log.insert(0,"Engine error: Workflow (%s) initialization failed: %s" % (wf_name, traceback.format_exc()))#	str(e)), "WF Engine")
			global_log.insert(0,"<br/>"+Trace.exception_trace())
			return False, "Engine error: Workflow (%s) initialization failed: %s" % (wf_name, str(e))

	def unload_workflow(self, wf_name, folder_guids = []):
		if "%s.%s"%(application.id, wf_name) in sys.modules and wf_name:
			sys.modules.pop("%s.%s"%(application.id, wf_name))

		listeners = {folder: (folder, operation) for folder, operation in engine.folder_trigers}
		for guid in folder_guids:
			if guid in listeners:
				del self.folder_trigers[listeners[guid]]

	def add_action(self, user_id, xml_response):
		self.actions_lock.lock()
		try:
			if user_id in self.actions:	self.actions[user_id].append(xml_response)
			else: 						self.actions[user_id] = [xml_response]
		finally:
			self.actions_lock.unlock()

	def get_actions(self, user_id):
		self.actions_lock.lock()
		actions = []
		try:
			if user_id in self.actions:
				actions = self.actions[user_id]
				self.actions[user_id] = []
		finally:
			self.actions_lock.unlock()

		return actions

	def clear_all(self):
		self.listeners 			= {}
		self.folder_trigers 	= {}
		self.file_locks 		= {}
		self.running_workflows 	= {}
		self.workflow_log 		= {}
		try:
			while True:
				workflow_queue.get_nowait()
		except Queue.Empty:
			pass


engine = WorkflowEngine()

