from utils.uuid import uuid4
from class_CRUD import CRUD
from helper import encode_xml, decode_xml
from class_workflow_instance import WorkflowInstance
from class_share_dir import ShareDir
from xml.dom.minidom import parseString
import os.path, os

from engine_compiler import Workflow_generator
from engine_core import engine

TABLE_NAME = "workflow"


class Workflow(object):

	def __init__(self):
		self.id	= None
		self.guid = None
		self.name = ""
		self.description = ""
		self.status = 0			# 0:stopped, 1:pause, 2:active
		self.modified = False
		self.error	= False
		self.source	= ""
		self.image = ""
		self.folder = ""
		self.compilable = False

		self._current_compilation = ""
		self._complete_path = ""
		self._share_dir		= None

	# define source
	@property
	def current_compilation(self):
		""" returns workflow class path """
		if not self._current_compilation:
			if os.path.exists(self.complete_path):
				self._current_compilation = self.complete_path
			else:
				raise Exception("CLASS_NEED_TO_BE_GENERATED")
		return self._current_compilation

	@property
	def complete_path(self):
#		full_path = os.path.join(VDOM_CONFIG["LIB-DIRECTORY"],application.id)
#		if not os.path.exists(full_path):
#			os.makedirs(full_path)

		if self._complete_path is "":
			self._complete_path = os.path.join(
				VDOM_CONFIG["LIB-DIRECTORY"],
				application.id,
#				"WORKFLOWS_CLASSES",
				self.name+".py"
			)
		return self._complete_path

	@property
	def share_dir(self):
		if self._share_dir != "":
			dir = ShareDir.get_by_guid(self.folder)
			self._share_dir = dir if isinstance(dir, ShareDir) else ""
		return self._share_dir

	@share_dir.setter
	def share_dir(self, value):
		if isinstance(value, ShareDir):
			self.folder = value.guid
			self._share_dir = value
		else:
			raise Exception("INPUT_MUST_BE_SHARE_DIR_INCTANCE")


	def __fill_from_row(self, row):
		self.id 			= row[0]
		self.name 			= row[1]
		self.status 		= row[2]
		self.modified 		= bool(row[3])
		self.error 			= bool(row[4])
		self.source 		= decode_xml(row[5])
		self.description	= row[6]
		self.guid			= row[7]
		self.image			= row[8]
		self.folder			= row[9]
		self.compilable		= bool(row[10])
		return self

	def __insert(self):
		self.guid = str(uuid4())
		self.id = CRUD.create(	TABLE_NAME,
								name=self.name,
								description=self.description,
								status=self.status,
								modified=int(self.modified),
								error=int(self.error),
								source=encode_xml(self.source),
								guid=self.guid,
								image=self.image,
								folder=self.folder,
								compilable=int(self.compilable)
							)
		return self

	@classmethod
	def __retrieve(self, **kwargs):
		rows = CRUD.retrieve(TABLE_NAME, **kwargs)
		return [Workflow().__fill_from_row(row) for row in rows]

	@classmethod
	def __retrieve_one(self, **kwargs):
		row = CRUD.retrieve_one(TABLE_NAME, **kwargs)
		return Workflow().__fill_from_row(row) if row else None

	def __update(self):
		CRUD.update(	TABLE_NAME,
						{"id": self.id},
						name=self.name,
						description=self.description,
						status=self.status,
						modified=int(self.modified),
						error=int(self.error),
						source=encode_xml(self.source),
						guid=self.guid,
						image=self.image,
						folder=self.folder,
						compilable=int(self.compilable)
					)
		return self

	def __delete(self):
		CRUD.delete(TABLE_NAME, guid=self.guid)
		return self


	def __is_unique(self):
		wf = Workflow().get_by_name(self.name)
		return bool( wf in (None, self) )


	def __eq__(self, other):
		return isinstance(other, Workflow) and self.id == other.id

	def __ne__(self, other):
		return not (isinstance(other, Workflow) and self.id == other.id)

	def __unicode__(self):

		lines_str =  [	u"* Workflow object *",
						u"General info:",
						u"\tid: %s" 			% self.id,
						u"\tguid: %s"			% self.guid,
						u"\tname: %s"			% self.name,
						u"\tdescription: %s"	% self.description,
						u"\tcompilable: %s"		% self.compilable,
						u"\tstatus: %s"			% self.status,
						u"\tmodified: %s"		% self.modified,
						u"\terror: %s"			% self.error,
						u"\tfolder: %s"			% self.folder,
						u"\timage: %s"			% self.image,
						u"Source:",
						self.source		]

		return "\n".join(lines_str)


	def save(self):

		# check if name is blank
		if not self.name:
			raise Exception("workflow name is blank")

		# check uniqueness
		if not self.__is_unique():
			raise Exception("workflow with the same name already exists")

		# save
		if self.id:	self.__update()
		else:		self.__insert()

		return self

	def delete(self):
		return self.__delete()

	@staticmethod
	def all():
		return Workflow().__retrieve()

	@staticmethod
	def get_by_id(class_id):
		return Workflow().__retrieve_one(id=class_id)

	@staticmethod
	def get_by_guid(class_guid):
		return Workflow().__retrieve_one(guid=class_guid)

	@staticmethod
	def get_by_name(class_name):
		return Workflow().__retrieve_one(name=class_name)

	def processes(self):
		return WorkflowInstance.get_by_workflow_guid(self.guid)

	def folders(self):
		""" returns a list of folders (ShareDir objects) used by a current workflow """

		# get workflow source
		source_doc = parseString(self.source.encode("utf-8"))

		# get folders names
		elem_folders = source_doc.getElementsByTagName("folders")[0]
		elems_folder = source_doc.getElementsByTagName("folder")

		top_folder_name = elem_folders.attributes["topLevelFolder"].value
		folder_names = [ef.attributes["name"].value for ef in elems_folder]

		# find folders
		top_folder = ShareDir.get_top_by_name(top_folder_name)
		folders = [ShareDir.get_by_name(top_folder, fname) for fname in folder_names]

		return [top_folder] + folders


	def generate_class(self):
		""" Generating workflow class and save it on the server in the file """

		wf_gen = Workflow_generator()
		wf_class = wf_gen.generate(self.source.encode("utf8"))

		f = open(self.complete_path, "w")
		f.write(wf_class.encode("utf8"))
		f.close()

	def activate(self):
		result, comment = False, ""
		if self.current_compilation:
			(result, comment) = engine.load_workflow(self.name)
		if result:
			self.status = 2
			self.save()
		return result, comment

	def deactivate(self):
		engine.unload_workflow(self.name)
		self.status = 0
		self.save()

	def pause(self):
		pass

""" Workflow Class
Contain data on workflow template. """
from utils.uuid import uuid4 from class_CRUD import CRUD from helper import encode_xml, decode_xml from class_workflow_instance import WorkflowInstance from class_share_dir import ShareDir from xml.dom.minidom import parseString import os.path, os
