from xml.dom.minidom import Document
TABLE_NAME = "workflow"
class Workflow(object):
def __init__(self): self.id = None self._name = None self.source = self.__get_default_source() self.guid = None self.modified = False self.error = False self.image = "" self.compilable = False # self.status = 0 # 0:stopped, 1:pause, 2:active
self._current_compilation = "" self._complete_path = "" self._label = None self._description = None self._folder = None self._folders = None
@property def name(self): if self._name == None: self._name = parseString(self.source.encode("utf-8")).getElementsByTagName("workflow")[0].attributes["name"].value return self._name
@name.setter def name(self, value): doc = parseString(self.source.encode("utf-8")) doc.getElementsByTagName("workflow")[0].attributes["name"].value = value self.source = doc.toxml() self._name = value
@property def label(self): if self._label == None: self._label = parseString(self.source.encode("utf-8")).getElementsByTagName("workflow")[0].attributes["label"].value return self._label
@label.setter def label(self, value): doc = parseString(self.source.encode("utf-8")) doc.getElementsByTagName("workflow")[0].attributes["label"].value = value self.source = doc.toxml() self._label = value
@property def description(self): if self._description == None: elem_descr = parseString(self.source.encode("utf-8")).getElementsByTagName("description")[0] self._description = elem_descr.firstChild.nodeValue if elem_descr.firstChild else "" return self._description
@description.setter def description(self, value): doc = parseString(self.source.encode("utf-8")) doc.getElementsByTagName("description")[0].childNodes = [doc.createCDATASection(value)] self.source = doc.toxml() self._description = value
# @property # def folder(self, return_string=False): # """ returns a top-level folder (ShareDir object) used by the current workflow """ # elem_folders = parseString(self.source.encode("utf-8")).getElementsByTagName("folders")[0] # folder_name = elem_folders.attributes["topLevelFolder"].value if elem_folders.hasAttribute("topLevelFolder") else None # return ShareDir.get_top_by_name(folder_name) if not return_string else folder_name
@property def folder(self): """ returns a top-level folder (ShareDir object) used by the current workflow """ if self._folder == None: elem_folders = parseString(self.source.encode("utf-8")).getElementsByTagName("folders")[0] folder_name = elem_folders.attributes["topLevelFolder"].value if elem_folders.hasAttribute("topLevelFolder") else None folder = ShareDir.get_top_by_name(folder_name) if folder: self._folder = folder else: self._folder = "" #raise Exception("WORKFLOW_NO_SUCH_FOLDER: %s"%str(folder_name)) return self._folder
@folder.setter def folder(self, share_dir): doc = parseString(self.source.encode("utf-8")) doc.getElementsByTagName("folders")[0].setAttribute("topLevelFolder", share_dir.name) self.source = doc.toxml() self._folder = share_dir
@property def folders(self, return_string=False): """ returns a list of 2nd level folders (ShareDir objects) used by the current workflow """ if self._folders == None: elems_folder = parseString(self.source.encode("utf-8")).getElementsByTagName("folder") folder_names = [ef.attributes["name"].value for ef in elems_folder] self._folders = [ShareDir.get_by_name(self.folder, fname) for fname in folder_names] return self._folders
@folders.setter def folders(self, share_dir_list):
def create_elem_folder(share_dir): elem_folder = Document().createElement("folder") elem_folder.setAttribute("name", share_dir.name) return elem_folder
doc = parseString(self.source.encode("utf-8")) doc.getElementsByTagName("folders")[0].childNodes = [create_elem_folder(d) for d in share_dir_list] self.source = doc.toxml() self._folders = share_dir_list
# define source @property def current_compilation(self): """ returns workflow class path """ if not self._current_compilation: if os.path.exists(self.complete_path): self._current_compilation = self.complete_path else: raise Exception("CLASS_NEED_TO_BE_GENERATED") return self._current_compilation
@property def complete_path(self): # full_path = os.path.join(VDOM_CONFIG["LIB-DIRECTORY"],application.id) # if not os.path.exists(full_path): # os.makedirs(full_path)
if self._complete_path is "": self._complete_path = os.path.join( VDOM_CONFIG["LIB-DIRECTORY"], application.id, # "WORKFLOWS_CLASSES", self.name+".py" ) return self._complete_path
@property def status(self): from engine_core import engine folderlist = [folder.guid for folder in self.folders if isinstance(folder, ShareDir)] listeners = [folder for folder, operation in engine.folder_trigers] return 2 if len([folder for folder in folderlist if folder in listeners])>0 else 0
def __fill_from_row(self, row): self.id = row[0] self._name = row[1] self.source = decode_xml(row[2]) self.guid = row[3] self.modified = bool(row[4]) self.error = bool(row[5]) self.image = row[6] self.compilable = row[7] return self
def __insert(self): self.guid = str(uuid4()) self.id = CRUD.create( TABLE_NAME, name=self.name, modified=int(self.modified), error=int(self.error), source=encode_xml(self.source), guid=self.guid, image=self.image, compilable=int(self.compilable) ) return self
@staticmethod def __retrieve(**kwargs): rows = CRUD.retrieve(TABLE_NAME, **kwargs) return [Workflow().__fill_from_row(row) for row in rows]
@staticmethod def __retrieve_one(**kwargs): row = CRUD.retrieve_one(TABLE_NAME, **kwargs) return Workflow().__fill_from_row(row) if row else None
def __update(self): CRUD.update( TABLE_NAME, {"id": self.id}, name=self.name, modified=int(self.modified), error=int(self.error), source=encode_xml(self.source), guid=self.guid, image=self.image, compilable=int(self.compilable) ) return self
def __delete(self): CRUD.delete(TABLE_NAME, guid=self.guid) return self
def __get_default_source(self): """ Returns an 'empty' workflow source (as a unicode string) filled only with initial data as follows:
<?xml version="1.0" ?> <workflow name="" label="" timeout="true" timeoutDelay="0:0:0:0" version="1.0"> <description></description> <folders autoCreateFolders="true"/> <graph> <steps/> <transitions/> </graph> </workflow> """
# create an XML document doc = Document()
# create <workflow> element elem_wf = doc.createElement("workflow") elem_wf.setAttribute("name", "") elem_wf.setAttribute("label", "") elem_wf.setAttribute("timeout", "true") elem_wf.setAttribute("timeoutDelay", "0:0:0:0") elem_wf.setAttribute("version", "1.0") doc.appendChild(elem_wf)
# create <description> element elem_descr = doc.createElement("description") elem_wf.appendChild(elem_descr)
value_descr = doc.createCDATASection("") elem_descr.appendChild(value_descr)
# create <folders> element elem_folders = doc.createElement("folders") elem_folders.setAttribute("autoCreateFolders", "true") elem_wf.appendChild(elem_folders)
# create <graph> element elem_graph = doc.createElement("graph") elem_wf.appendChild(elem_graph)
# create <steps> element elem_steps = doc.createElement("steps") elem_graph.appendChild(elem_steps)
# create <transitions> element elem_trans = doc.createElement("transitions") elem_graph.appendChild(elem_trans)
# return created XML doc return doc.toprettyxml()
def __is_unique(self): wf = Workflow.get_by_name(self.name) return bool( wf in (None, self) )
def __eq__(self, other): return isinstance(other, Workflow) and self.id == other.id
def __ne__(self, other): return not (isinstance(other, Workflow) and self.id == other.id)
def __unicode__(self):
lines_str = [ u"* Workflow object *", u"General info:", u"\tid: %s" % self.id, u"\tguid: %s" % self.guid, u"\tname: %s" % self.name, u"\tlabel: %s" % self.label, u"\tdescription: %s" % self.description, u"\tfolder: %s" % (self.folder.name if self.folder else None), u"\tfolders: %s" % ", ".join([f.name if f else "None" for f in self.folders]), u"\tcompilable: %s" % self.compilable, u"\tstatus: %s" % self.status, u"\tmodified: %s" % self.modified, u"\terror: %s" % self.error, u"\timage: %s" % self.image, u"Source:", self.source ]
return "\n".join(lines_str)
def save(self):
# check if name is blank if not self.name: raise Exception("workflow name is blank")
# check uniqueness if not self.__is_unique(): raise Exception("workflow with the same name already exists")
# save if self.id: self.__update() else: self.__insert()
return self
def delete(self): return self.__delete()
@staticmethod def all(): return Workflow().__retrieve()
@staticmethod def get_by_id(class_id): return Workflow().__retrieve_one(id=class_id)
@staticmethod def get_by_guid(class_guid): return Workflow().__retrieve_one(guid=class_guid)
@classmethod def get_by_name(self, class_name): import managers managers.log_manager.info_bug(str(Workflow) , "WF Engine") return self.__retrieve_one(name=class_name)
def processes(self): return WorkflowInstance.get_by_workflow_guid(self.guid)
def generate_class(self): """ Generating workflow class and save it on the server in the file """
wf_gen = Workflow_generator() wf_class = wf_gen.generate(self.source.encode("utf8"))
f = open(self.complete_path, "w") f.write(wf_class.encode("utf8")) f.close()
def activate(self): result, comment = False, "" if self.current_compilation: (result, comment) = engine.load_workflow(self.name) # if result: # self.status = 2 # self.save() return result, comment
def deactivate(self): engine.unload_workflow(self.name, [folder.guid for folder in self.folders if isinstance(folder, ShareDir)]) # self.status = 0 # self.save()
def pause(self): pass

from class_db import Database
from utils.uuid import uuid4
from class_ACL_proxy import ACL_proxy, ACL_record

class SmartFolder(object):

	def __init__(self):
		self.id = None
		self.guid = ""
		self.name = ""

		self.__sub_folders = None
		self._acl_subjects	= None # groups and users in one list, those who have access to this object

	def __init_from_db(self, row):
		self.id 	= row[0]
		self.guid 	= row[1]
		self.name 	= row[2]
		return self

	def __insert(self):
		self.guid = str(uuid4())
		query = """INSERT INTO smart_folder(name, guid)
					VALUES(?,?)"""
		rows = Database.maindb().commit(query,(self.name, self.guid),)

	def __update(self):
		query = """
			UPDATE 	smart_folder
			SET 	name=?
			WHERE 	guid=?
		"""
		rows = Database.maindb().commit(query,(self.name, self.guid),)

	def __delete(self):
		query = """DELETE FROM smart_folder WHERE id=? """
		Database.maindb().commit(query, (str(self.id),))

	def save(self):
		sf = self.get_by_name(self.name)
		if sf and sf.id != self.id:
			raise Exception("SAME_NAME_ERROR_SMART_FOLDER")

		if self.id:	self.__update()
		else:		self.__insert()

	def delete(self):
		if self.id:
			# delete metafield connected to this smart folder
			from class_metafield import Metafield
			smart_metas = Metafield.get_by_constraint(self.guid)
			if smart_metas:
				for sm in smart_metas:
					sm.delete()

			# delete sub-folders
			for ssf in self.sub_folders:
				ssf.delete()

			# delete itself from database
			self.__delete()

	@property
	def sub_folders(self):
		if self.__sub_folders == None:
			self.__sub_folders = SmartSubFolder.get_by_parent_guid(self.guid)

		return self.__sub_folders

	@classmethod
	def get_by_id(self, id):
		query = """SELECT id, guid, name
					FROM smart_folder
					WHERE `id` = ?"""
		row = Database.maindb().fetch_one(query, (str(id),))
		return SmartFolder().__init_from_db(row) if row else None

	@classmethod
	def get_by_guid(self, guid):
		query = """SELECT id, guid, name
					FROM smart_folder
					WHERE `guid` = ?"""
		row = Database.maindb().fetch_one(query, (str(guid),))

		return SmartFolder().__init_from_db(row) if row else None

	@classmethod
	def get_by_name(self, name):
		query = """SELECT id, guid, name
					FROM smart_folder
					WHERE `name` = ?"""
		row = Database.maindb().fetch_one(query, (name,))

		return SmartFolder().__init_from_db(row) if row else None

	@classmethod
	def all(self):
		query = """SELECT id, guid, name
					FROM smart_folder
					ORDER BY `name`	"""
		rows = Database.maindb().fetch_all(query,)
#		raise Exception(str(rows))
		return [SmartFolder().__init_from_db(row) for row in rows]

	@property
	def acl_subjects(self):
		from class_user import User
		from class_group import Group
		if self._acl_subjects is None:
			acls = ACL_record.retrieve(object=self.guid)
			self._acl_subjects = [
				User.get_by_guid(acl.subject) or Group.get_by_guid(acl.subject) for acl in acls
			]
		return self._acl_subjects

	def users_acl(self):
		from class_user import User
		return [subject for subject in self.acl_subjects if isinstance(subject, User)]

	def groups_acl(self):
		from class_group import Group
		return [subject for subject in self.acl_subjects if isinstance(subject, Group)]

	def subjects_acl(self):
		return self.acl_subjects #[subject for subject in self.acl_subjects]

	def add_access_to(self, subject_guid, rule):
		new_acl = ACL_record()
		new_acl.subject = subject_guid
		new_acl.object 	= self.guid
		new_acl.rule 	= rule
		new_acl.save()

	def delete_access_for(self, subject_guid, rule=""):
		ACL_record.delete(
			subject=subject_guid, object=self.guid, rule=rule
		)

	def copy_acls_to(self, share_object):
		new_acl = ACL_record()
		new_acl.object 	= share_object.guid
		new_acl.rule 	= ACL_proxy.rules['SEE']

		for acl in self.subjects_acl():
			new_acl.subject = acl.guid
			new_acl.save()


