def get_attribute( self, attribute_name ): return self.attributes[attribute_name] if attribute_name in self.attributes else ' '
def add_attribute( self, name, value ): self.attributes.append({name: value})
def set_attribute( self, name, value ): if name in self.attributes: self.attributes[ name ] = value else: add_attribute( self, name, value )

import ldap
import ldap.modlist as modlist
from class_ldapobject import LDAPObject

class LDAPConnectionWriteException( Exception ):
	def __init__( self ):
		Exception.__init__(self, "LDAPConnection is read only")

class LDAPConnection:
	def __init__( self, editable = True ):
		self.editable = editable

	def bind(self, host, dn, passwd):
		self.connection = ldap.open( host )
		self.connection.simple_bind_s( dn, passwd )


	def unbind(self):
		self.connection.unbind()


	def save(self, ldapobject):
		""" save node - insert or update if node exist
		"""
		if self.exists( ldapobject ):
			return self.__update( ldapobject )
		else:
			return self.__insert( ldapobject )


	def __insert(self, ldapobject):
		""" insert to LDAP tree
		"""
		if self.editable:
			dn 			= self._encode( ldapobject.dn )
			attributes 	= self._encode( ldapobject.attributes )

			ldif = modlist.addModlist( attributes )
#			raise Exception( attributes )
#			raise Exception( ldif, dn, attributes )
			return self.connection.add_s( dn, ldif )
		else: return LDAPConnectionWriteException()


	def __update(self, ldapobject):
		""" update node in LDAP tree
		"""
		if self.editable:
			dn 			= self._encode( ldapobject.dn )
			attributes 	= self._encode( ldapobject.attributes )

			mod_attrs = []
			for attr_name, attr_value in attributes.iteritems():
				mod_attrs.append((ldap.MOD_REPLACE, attr_name, attr_value))

			for attr_name, attr_value in self.get_by_dn(dn).attributes.iteritems():
				if attr_name not in ldapobject.attributes:
					mod_attrs.append((ldap.MOD_DELETE, attr_name, None))

			return self.connection.modify_s(dn, mod_attrs)
		else: return LDAPConnectionWriteException()


	def search(self, basedn, filter, recursive = False ):
		""" search in LDAP tree
		"""
#		raise Exception( basedn, filter )
		basedn 	= self._encode( basedn )
		filter	= self._encode( filter )


		searchScope = ldap.SCOPE_SUBTREE if recursive else ldap.SCOPE_ONELEVEL
		retrieveAttributes = None

		result_set = []
		try:
			ldap_result_id = self.connection.search( basedn, searchScope, filter, retrieveAttributes )
#		if user:
#			raise Exception( ldap_result_id )
#			raise Exception( ldap_result_id )
			result_type, result_data = self.connection.result( ldap_result_id, 0 )
#			if user:
#				raise Exception( result_data )
			while result_data != []:
#				if user:
#					raise Exception( result_data )
	#			if a == 2:
	#				raise Exception( a, result_data )
				if result_type == ldap.RES_SEARCH_ENTRY:

					dn 			= self._decode( result_data[0][0] )
					attributes	= self._decode( result_data[0][1] )
					ldap_object = LDAPObject( dn, attributes )
	#					raise Exception( ldap_object.dn, ldap_object.attributes )
					result_set.append( ldap_object )
	#					raise Exception( result_set )
	#			raise Exception( result_set )
				result_type, result_data = self.connection.result( ldap_result_id, 0 )
			return result_set
		except ldap.LDAPError, e:
#			raise Exception( e, basedn, filter )
			return result_set



	def delete(self, ldapobject):
		if self.editable:
			dn = ldapobject.dn
			result = self.search( dn, "objectClass=*", True)
			sorted_result = sorted(result, cmp=lambda x,y: cmp(len(y.dn), len(x.dn)))

			for o in sorted_result:
				o_dn = self._encode( o.dn )
				self.connection.delete_s( o_dn )
		else: return LDAPConnectionWriteException()


	def create_object(self, dn, attributes ):
		return LDAPObject( dn, attributes )


	def exists( self, ldap_object ):
		return True if self.search( ldap_object.parent_dn, ldap_object.rdn, False ) else False


	def get_by_dn(self, dn ):
		obj = LDAPObject( dn, None )
#		if user:
#			raise Exception( obj.rdn, obj.parent_dn )
#		raise Exception( obj.parent_dn, obj.rdn )
		result = self.search( obj.parent_dn, obj.rdn, recursive=False )
		return result[0] if result else None




	def _encode( self, obj ):
		""" encode structure to utf-8 """

		if type( obj ) == unicode:
			return obj.encode( 'utf8' )

		elif type( obj ) == list:
			return [ self._encode( o ) for o in obj ]

		elif type( obj ) == dict:
			return { k:self._encode( obj[k] ) for k in obj }

		else:
			return obj


	def _decode( self, obj ):
		""" decode structure to unicode """

		if type( obj ) == str:
			return obj.decode( 'utf8' )

		elif type( obj ) == list:
			return [ self._decode( o ) for o in obj ]

		elif type( obj ) == dict:
			return { k:self._decode( obj[k] ) for k in obj }

		else:
			return obj

class LDAPIncorrectDN( Exception ):
	""" Exception in dn data in init of ldap object
	"""
	def __init__( self ):
		Exception.__init__( self, 'Incorrect DN data' )


class LDAPObject:
	def __init__(self, dn, attributes):
		"""
		"""
		# tree nodes of this LDAP object
		self._nodes = []
		self.set_dn( dn )

		# object attributes
		self.attributes = attributes


	def get_dn( self ):
		""" get dn-string of this LDAP object
		"""
		return unicode( ",".join( self._nodes ) )

	def set_dn( self, dn ):
		""" set dn. It can be list of nodes, or string.
		"""
		# fill nodes from dn
		if type( dn ) in [ str, unicode ]:
			self._nodes = dn.split( ',' )
		elif type( dn ) in [ list, tuple ]:
			self._nodes = ",".join( dn ).split( "," )
		else:
			raise LDAPIncorrectDN()


	def get_rdn( self ):
		return unicode( ",".join( self._nodes[:1] ) )

	def set_rdn( self, value ):
		"""
		"""
		self._nodes[0] = value


	def get_parent_dn(self):
		return unicode( ",".join( self._nodes[1:] ) )





	dn 			= property( get_dn, set_dn )
	rdn 		= property( get_rdn, set_rdn )
	parent_dn 	= property( get_parent_dn )


	def __eq__( self, other ):
		if not other: return False
		return 	self.attributes == other.attributes	and self.dn == other.dn

def scalar(key, defaultvalue = None, castto = None):
	return request.arguments.get(key, defaultvalue, castto)
	#    value = request.arguments[ key ] if key in request.argume
    notdecoded = request.arguments[ key ] if key in request.arguments else defaultvalue
    value = notdecoded.decode( 'utf8' ) if isinstance( notdecoded, str ) else notdecoded
    return castto(value) if castto and value else value

import Queue indexer_queue = Queue.Queue()
def index_document(doc_obj): indexer_queue.put(doc_obj)
def index_process_queue(self): while True: doc_obj = indexer_queue.get(True) import indexers index_function = indexers.get_mime_indexer( doc_obj.mime() ) doc_obj.__fulltext = index_function( doc_obj.absolute_physical_path() ) #returns UTF-8 !!! doc_obj.__fulltext = normalize( doc_obj.__fulltext ) time.sleep(0.2)

